package com.mc.books.fragments.home.relatedBook;

import android.content.Intent;
import android.net.Uri;
import android.os.Bundle;
import android.support.annotation.NonNull;
import android.support.annotation.Nullable;
import android.support.v7.app.ActionBar;
import android.support.v7.widget.GridLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.View;

import com.bon.customview.button.ExtButton;
import com.bon.customview.textview.ExtTextView;
import com.mc.adapter.AuthorAdapter;
import com.mc.books.R;
import com.mc.common.fragments.BaseMvpFragment;
import com.mc.models.home.Author;
import com.mc.models.home.RelatedBook;

import java.util.ArrayList;
import java.util.List;

import butterknife.BindView;
import butterknife.OnClick;

import static com.mc.utilities.Constant.KEY_RELETE_BOOK;

public class RelatedBookFragment extends BaseMvpFragment<IRelatedBookView, IRelatedBookPresenter<IRelatedBookView>> implements IRelatedBookView {
    public static RelatedBookFragment newInstance(RelatedBook relatedBook) {
        Bundle args = new Bundle();
        RelatedBookFragment fragment = new RelatedBookFragment();
        args.putSerializable(KEY_RELETE_BOOK, relatedBook);
        fragment.setArguments(args);
        return fragment;
    }

    @BindView(R.id.txtGeneral)
    ExtTextView txtGeneral;
    @BindView(R.id.txtReadMore)
    ExtTextView txtReadMore;
    @BindView(R.id.rvAuthor)
    RecyclerView rvAuthor;
    @BindView(R.id.btnBuyBook)
    ExtButton btnBuyBook;
    private RelatedBook relatedBook;
    private boolean isReadMoreGeneal;
    private AuthorAdapter authorAdapter;

    @NonNull
    @Override
    public IRelatedBookPresenter<IRelatedBookView> createPresenter() {
        return new RelatedBookPresenter<>(getAppComponent());
    }

    @Override
    public int getResourceId() {
        return R.layout.detail_related_book_fragment;
    }

    @Override
    public void onViewCreated(View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        bindButterKnife(view);
        relatedBook = (RelatedBook) getArguments().getSerializable(KEY_RELETE_BOOK);
        initData();
    }

    @Override
    public String getTitleString() {
        if (relatedBook != null)
            return relatedBook.getName();
        return "";
    }

    @Override
    public void initToolbar(@NonNull ActionBar supportActionBar) {
        supportActionBar.setDisplayHomeAsUpEnabled(true);
        supportActionBar.setHomeAsUpIndicator(R.drawable.ic_arrow_right);
    }

    private void initData() {
        txtGeneral.setText("");
        txtGeneral.post(() -> {
            int lineCount = txtGeneral.getLineCount();
            if (lineCount > 3) {
                txtGeneral.setMaxLines(3);
                txtReadMore.setVisibility(View.VISIBLE);
            }
        });

        authorAdapter = new AuthorAdapter(getAppContext());
        rvAuthor.setLayoutManager(new GridLayoutManager(getAppContext(), 2));
        rvAuthor.setAdapter(authorAdapter);
        rvAuthor.setNestedScrollingEnabled(false);
        List<Author> authorList = new ArrayList<>();
        authorList.add(new Author("Châu Nhật Cường", "data:image/jpeg;base64,"));
        authorList.add(new Author("Châu Nhật Cường", "data:image/jpeg;base64,"));
        authorList.add(new Author("Châu Nhật Cường", "data:image/jpeg;base64,"));
        authorAdapter.setDataList(authorList);
    }

    @OnClick({R.id.txtReadMore, R.id.btnBuyBook})
    public void onViewClicked(View view) {
        switch (view.getId()) {
            case R.id.txtReadMore:
                if (isReadMoreGeneal) {
                    isReadMoreGeneal = false;
                    txtGeneral.setMaxLines(3);
                    txtReadMore.setText(R.string.show_more);
                } else {
                    isReadMoreGeneal = true;
                    txtGeneral.setMaxLines(99);
                    txtReadMore.setText(R.string.show_less);
                }
                break;
            case R.id.btnBuyBook:
                startActivity(new Intent(Intent.ACTION_VIEW, Uri.parse("https://www.youtube.com/watch?v=_pvLNlI1GLw&index=12&list=RDMMo7bZThLaWP8")));
                break;
        }
    }
}
